import { useQuery } from "@tanstack/react-query";
import { useContext, useEffect, useState } from "react";
import { useLocation } from "react-router-dom";
import Popup from "reactjs-popup";
import MainHeader from '../../components/mainHeader';
import UserProfile from '../../components/userProfile';
import Post from "../post/post";
import EditProfileForm from "./editProfileForm";
import { useGetUserByUsername } from "./useGetUserByUsername";
import { useGetUserPostsById } from "./useGetUserPostsById";
import { useFollowMutations } from "./useFollowMutations";
import { useUpdateUserMutation } from "./useUpdateUserMutation";
import { useProfilePostsQuery } from "./useProfilePostsQuery";
import { useIsFollowedQuery } from "./useIsFollowedQuery";
import { request } from "../../utils/axios";
import { AuthContext } from "../../contexts/authContext";

const overlayStyle = {
    'background': 'rgba(255,255,255,0.1)',
    'display': 'flex',
    'justifyItems': 'center',
    'alignItems': 'center',
};

const ProfilePage = () => {
    const location = useLocation();
    const username = location.pathname.slice(1);
    const [openModal, setOpenModal] = useState(false);
    const updateUserMutation = useUpdateUserMutation();
    
    const { 
        data: user,
        isFetching: isFetchingUser
    } = useQuery(['profileUser', { username }], () => 
    request.get('/user/' + username).then(res => res.data[0]));

    const { follow, unfollow } = useFollowMutations(user?.id);
    
    const { 
        data: posts, 
        isFetching: isFetchingPosts 
    } = useProfilePostsQuery(user?.id, !!user );
    
    const isFollowed = useIsFollowedQuery(user?.id, !!user);

    const handleFollow = () => {
        isFollowed ? unfollow() : follow()
    }

    useEffect(() => {
        window.scroll(0, 0);
    }, [])

    if(isFetchingUser || isFetchingPosts) return <></>

    return ( 
        <> 
            <MainHeader>
                <p>{user.name}</p>
            </MainHeader>
            <UserProfile 
                user={user} 
                isFollowed={isFollowed} 
                handleFollow={handleFollow}
                onOpenModal={() => setOpenModal(true)}
                />           
            <Popup
                open={openModal}
                onOpen={() => setOpenModal(true)}
                onClose={() => setOpenModal(false)}
                {...{overlayStyle}}
                >   
                <EditProfileForm 
                    onSubmit={(data) => updateUserMutation.mutate(data, {
                        onSuccess: () => setOpenModal(false)
                    })}
                    onCloseModal={() => setOpenModal(false)}
                    />
            </Popup>
            {posts.map(post => 
                <div 
                    className='border-b border-stone-700'
                    key={post.id}
                    >
                    <Post post={post}/>
                </div>
            )}
        </>
    )
}

export default ProfilePage;
