import { useQuery } from "@tanstack/react-query";
import { useGetPostByIdQuery } from "./useGetPostByIdQuery";

export const useGetThreadQuery = ({ id }) => {

        const getPostThread = async id => {
        const posts = [];
        let post = await getPostById(id);
        while(post.parent_id) {
            post = await getPostById(post.parent_id);
            posts.unshift(post);
        }
        return posts;
    }
    
    return useQuery(['postThread', { id }], () => 
        getPostThread(id), {
            enabled: !!id
        }); 
}
