import {
    useQuery,
    useQueryClient
} from "@tanstack/react-query";
import { useContext, useEffect, useState } from "react";
import { useLocation } from "react-router-dom";
import Popup from "reactjs-popup";
import MainHeader from '../../components/mainHeader';
import UserProfile from '../../components/userProfile';
import { AuthContext } from "../../contexts/authContext";
import { getUserPostsById } from "../../services/postsService";
import { getUserByUsername } from "../../services/userService";
import Post from "../post/post";
import EditProfileForm from "./editProfileForm";
import { useFollowUser } from "./useFollowUser";
import { useUpdateUser } from "./useUpdateUser";

const overlayStyle = {
    'background': 'rgba(255,255,255,0.1)',
    'display': 'flex',
    'justifyItems': 'center',
    'alignItems': 'center',
};

const ProfilePage = () => {
    const location = useLocation();
    const username = location.pathname.slice(1);
    const { currentUser } = useContext(AuthContext);
    const queryClient = useQueryClient();
    const [openModal, setOpenModal] = useState(false);
    const { updateUser } = useUpdateUser({
        onUpdateSucess: () => setOpenModal(false) 
    });
    
    const { 
        data: user, 
        isFetching: isFetchingUser 
    } = useQuery(['profileUser', { username }], () => 
    getUserByUsername(username));

    const { isFollowed, follow, unfollow } = useFollowUser(user || '');
    
    const { 
        data: posts,  
        isFetching: isFetchingPosts
    } = useQuery(['profilePosts', { user_id: user?.id }], () => 
    getUserPostsById(user.id), {
        enabled: !!user
    }
    );

    const handleFollow = () => {
        isFollowed ? unfollow() : follow()
    }

    useEffect(() => {
        window.scroll(0, 0);
    }, [])

    if(isFetchingUser || isFetchingPosts) return <></>

    return ( 
        <> 
            <MainHeader>
                <p>{user.name}</p>
            </MainHeader>
            <UserProfile 
                user={user} 
                isFollowed={isFollowed} 
                handleFollow={handleFollow}
                onOpenModal={() => setOpenModal(true)}
                />           
            <Popup
                open={openModal}
                onOpen={() => setOpenModal(true)}
                onClose={() => setOpenModal(false)}
                {...{overlayStyle}}
                >   
                <EditProfileForm 
                    onSubmit={(data) => updateUser(data).then(() => {
                        setOpenModal(false)
                    })
                    }
                    onCloseModal={() => setOpenModal(false)}
                    />
            </Popup>
            {posts.map(post => 
                <div 
                    className='border-b border-stone-700'
                    key={post.id}
                    >
                    <Post post={post}/>
                </div>
            )}
        </>
    )
}

export default ProfilePage;
