import { useQuery } from "@tanstack/react-query";
import { request } from "../../utils/axios";

export const useIsFollowedQuery = (id, enabled=true) =>
    useQuery(['follow'], () => 
        request.get('/follow', {
            params: {
                followed_user_id: id
            }
        }).then(res => 
            res.data.filter(follower => 
                follower.follower_user_id === currentUser.id).length
        ), {
            enabled: enabled
    })
