import 'moment/locale/pt-br';
import { useState } from "react";
import Popup from 'reactjs-popup';
import { baseURL } from '../../utils/constants';
import PostActions from "./postActions";
import PostHeader from "./postHeader";
import WriteComment from "./writeComment";

const overlayStyle= {
    'background': 'rgba(255,255,255,0.1)',
    'display': 'flex',
    'justifyItems': 'center',
    'alignItems': 'center',
};

const Post = ({ postObj }) => {
    const [commentPopup, setCommentPopup] = useState(false);
    
    const { 
        id,
        ingredients, 
        description, 
        file,
    } = postObj;

    return (
        <div key={id}>
            <PostHeader postObj={postObj}>
                {ingredients && ingredients.length > 0 && 
                    <div className="flex flex-col">
                        <p className="font-medium mt-1">{'Ingredientes: '}</p>
                        {ingredients.map((ingredient, index) => (
                            <p key={index} className="inline">  {
                                    ingredient.quantity + " " + 
                                    ingredient.unity + " " + 
                                    ingredient.name
                                }
                            </p>
                        ))}
                    </div>
                }
                {description && 
                    <div>
                        <p className="font-medium mt-2">{'Descrição: '}</p>
                        <p>{description}</p>
                    </div>
                }
                {file && <img className="rounded-xl max-h-[500px] mt-4" src={baseURL + '/upload/post/' + file} alt=''/>}
                <PostActions post_id={id} onClickComment={() => setCommentPopup(true)}/>
            </PostHeader>
            <Popup
                open={commentPopup}
                onOpen={() => setCommentPopup(true)}
                onClose={() => setCommentPopup(false)}
                {...{overlayStyle}}
                >   
                <WriteComment closePopup={() => setCommentPopup(false)} postObj={postObj}/>
            </Popup>
        </div>
    )
}

export default Post;
