import { useMutation } from "@tanstack/react-query"
import axios from "axios"
import { request } from "../../utils/axios"

const url = 'http://localhost:5000';

export const useCreateComment = () => {
    return useMutation(async ({ post_id, user_id, comment_text }) => 
        await axios.post(url + '/comments'), {
            post_Id,
            user_id,
            comment_text
        })
}

export const getComments = async (post_id) => {
    const res = await request.get('/comments', {
        params: {
            post_id,
        }
    })
    return res.data;
}

export const createComment = async ({ post_id, user_id, comment_text }) => 
    await request.post('/comments', {
        post_id,
        user_id,
        comment_text
    })

export const deleteComment = async (post_id) => 
    await request.delete('/comments', {
        data: {
            post_id
    }})
