import { useMutation, useQuery, useQueryClient } from "@tanstack/react-query";
import moment from "moment";
import 'moment/locale/pt-br';
import { useContext } from "react";
import { Link, useNavigate } from "react-router-dom";
import { AuthContext } from "../../contexts/authContext";
import { deletePost } from "../../services/postsService";
import { getUserById } from "../../services/userService";
import { baseURL } from '../../utils/constants';

const PostHeader = ({ children, postObj }) => {
    const { currentUser } = useContext(AuthContext);
    const queryClient = useQueryClient();
    const navigate = useNavigate();
    
    const { 
        id,
        user_id, 
        date, 
        title, 
    } = postObj;

    const { data: user, isFetched: isFetchedUser } = useQuery(
        ['postUser', { user_id }], () => getUserById(user_id)
    );

    const deleteMutation = useMutation(
        () => deletePost(id), {
            onSuccess: () => {
                queryClient.invalidateQueries(['posts']);
                queryClient.invalidateQueries(['profilePosts']);
            }
        }
    )
    return (
        <div className="flex flex-row pt-2 pr-3 " key={id}>
            <div className="w-20 flex justify-center">  
                {isFetchedUser && <img 
                    className='w-12 h-12 rounded-[40px]' 
                    src={baseURL + '/upload/user/' + user.profile_img} 
                    alt=''
                    />}
            </div>
            <div className="w-full">
                <div className="flex justify-between pr-5">
                    <div className="flex gap-2 items-center">
                        {isFetchedUser && 
                        <p className="font-bold hover:underline hover:cursor-pointer">
                            {user.name}
                        </p>}
                        <div onClick={() => navigate('/' + user.username)} className="hover:cursor-pointer flex flex-row text-sm text-stone-400 gap-1">
                            {isFetchedUser && <p>{'@' + user.username}</p>}
                            <p> 
                                {"· " + moment(date, 'YYYY-MM-DD HH:mm:ss')
                                .fromNow(true)}
                            </p>
                        </div>
                    </div>
                    {isFetchedUser && user.id === currentUser.id && 
                        <button
                            onClick={() => deleteMutation.mutate()}
                            >
                            ...
                        </button>
                    } 
                </div>
                <div onClick={() => navigate('/post/' + postObj.id)} className='hover:cursor-pointer '>
                    <p className="text-xl my-1">{title}</p>
                    {children}
                </div>
            </div>
        </div>
    )
}

export default PostHeader;
