import { QueryClient, QueryClientProvider } from "@tanstack/react-query";
import { BrowserRouter, Route, Routes } from "react-router-dom";
import MainLayout from '../components/mainLayout';
import AuthProvider from '../contexts/authContext';
import PostPage from "./post/postPage";
import LoginPage from '../containers/auth/loginController'
import SignupController from "../containers/auth/signupController";
import HomePage from "./post/homePage";
import ProfileController from "./profile/profileController";
import ExplorePage from './explorePage'
import BookmarksPage from './bookmarksPage'
import SettingsPage from './settingsPage'

const queryClient = new QueryClient({
  defaultOptions: {
    queries: {
      refetchOnWindowFocus: false,  
    },
  },
})

const App = () => (
  <QueryClientProvider client={queryClient}>
    <AuthProvider>
      <BrowserRouter>
        <Routes>
          <Route path='/' element={<MainLayout/>}>
            <Route index element={<HomePage/>}/>
            <Route path='explore' element={<ExplorePage/>}/>
            <Route path='/:username' element={<ProfileController/>}/>
            <Route path='bookmarks' element={<BookmarksPage/>}/>
            <Route path='settings' element={<SettingsPage/>}/>
            <Route path='/post/:post_id' element={<PostPage/>}/>
          </Route>
          <Route path='/login' element={<LoginPage/>}/>
          <Route path='/register' element={<SignupController/>}/>
        </Routes>
      </BrowserRouter>
    </AuthProvider>
  </QueryClientProvider>
);

export default App;
