import { useQuery } from "@tanstack/react-query";
import { useGetPostByIdQuery } from "./useGetPostByIdQuery";

export const useGetThreadQuery = ({ id }) => 
    useQuery(['postThread', { id }], () => {
        const posts = [];
        let post = useGetPostByIdQuery({ id });
        while(post.parent_id) {
            post = useGetPostByIdQuery({ id: post.parent_id });
            posts.unshift(post);
        }
        return posts;
    }
);
