import { useQuery } from "@tanstack/react-query";
import { request } from "../../utils/axios";

export const useGetUserQuery = (id) => {

    const { data, isFetching } = useQuery(['user', { id }], () =>
        request.get('/user', {
            params: { id }
        }).then(res => res.data[0]), {
            enabled: !!id
        }
    );

    return { user: data, isFetchingUser: isFetching }
}
