import { useMutation } from "@tanstack/react-query";
import { useContext } from "react";
import { useForm } from "react-hook-form";
import Input from "../../components/input";
import { AuthContext } from "../../contexts/authContext";
import { baseURL } from "../../utils/constants";
import { createComment } from "./commentService";

const Comment = ({ post, user, closePopup }) => {
    const { handleSubmit, register, reset } = useForm();
    const { currentUser } = useContext(AuthContext);

    const onSubmit = (data) => {
        commentMutation.mutate(data).then(() => closePopup());
    }

    const commentMutation = useMutation(
        (data) => createComment({ 
            user_id: currentUser.id,
            post_id: post.id,
            comment_text: data.comment_text            
         }), {
            onSuccess: () => console.log('success')
         }
    );

    return(
        <form autoComplete="off" className="py-10 flex flex-col w-[600px] bg-stone-900 rounded-xl pb-14" onSubmit={handleSubmit(onSubmit)}>
            <p className="ml-4 mb-4 text-sm">{'Respondendo à ' + user.name}</p>
            <div className="grid grid-cols-[12px_auto] grid-rows-2 w-full">
                <img src={baseURL + '/upload/user/' + user.profile_img} alt='' className='row-start-1 col-start-1 w-12 h-12 rounded-[40px]'/>
                <img src={baseURL + '/upload/user/' + currentUser.profile_img} alt='' className='row-start-2 col-start-1 w-12 h-12 rounded-[40px]'/>
                <input 
                    placeholder={'Digite aqui ...'}
                    className={`col-start-2 row-span-2 placeholder:text-stone-500 rounded-md text-white align-middle outline-none h-14 bg-inherit text-xl py-1 pl-2 focus:placeholder:invisible focus:border-stone-500 hover:border-stone-600 transition-colors`}
                    {...register('comment_text')}
                    />
            </div>
            <button type='submit'>Enviar comentário</button>
        </form>
    )
}

export default Comment;
