import { useEffect, useState } from "react";
import { useGetPostByIdQuery } from "../../hooks/useGetPostByIdQuery";

export const useGetPostThread = async id => {
    const [posts, setPosts] = useState([]);
    const { data: post } = useGetPostByIdQuery({ id });

    const fetchPost = async id => 
        request.get('/single', {
            params: { id }
            }).then(res => {
                const post = res.data[0];
                return {
                    ...post, 
                    ingredients: JSON.parse(post.ingredients),
                    date: moment(post.date).format('YYYY-MM-DD HH:mm:ss')
                }
            })


    useEffect(() => {
        if(!posts) {
            const parentPost = fetchPost(post.parent_id);
            setPosts(posts => [...posts, parentPost]);
        }
        else {
            const parentPost = posts[posts.length - 1];
            if(parentPost.parent_id) {
                const parentPost = fetchPost(id);
                setPosts(posts => [...posts, parentPost]);
            }
        }

    }, [posts])

    return posts;
}
