import { useQuery } from "@tanstack/react-query";
import moment from "moment";
import { request } from "../../utils/axios";

export const useGetPostByIdQuery = (id) => {
    const { data, isFetching } = useQuery(['post', { id }], () => 
        request.get('/single', {
            params: { id }
            }).then(res => {
                const post = res.data[0];
                return {
                    ...post, 
                    ingredients: JSON.parse(post.ingredients),
                    date: moment(post.date).format('YYYY-MM-DD HH:mm:ss')
                }
            }), {
                enabled: !!id
            })

    return { post: data, isFetchingPost: isFetching }
}
