import { useEffect, useState } from "react";
import CommentButton from "../../components/commentButton";
import LikeButton from "../../components/likeButton";
import SaveButton from "../../components/saveButton";
import ShareButton from "../../components/shareButton";
import { getComments, getLikes } from "../../services/postsService";

const PostActions = ({ openPopup }) => {
    const [liked, setLiked] = useState(false);

    useEffect(() => {
        if(likes) {
            setLiked(likes.filter(like => like.user_id === currentUser.id).length ? true : false)
        }
    }, [likes])

    const { data: likes, isFetched: isFetchedLikes } = useQuery(
        ['postLike', { post_id: id }], () => getLikes(id)
        )

    const { data: comments, isFetched: isFetchedComments } = useQuery(
        ['postComments', { post_id: id }], () => getComments(id)
        )

    const likeMutation = useMutation(
        () => createLike({ post_id: id, user_id: currentUser.id }), {
            onSuccess: () => queryClient.invalidateQueries(['postLike', { post_id: id }])
        }
    )

    const deleteLikeMutation = useMutation(
        () => deleteLike({ post_id: id, user_id: currentUser.id }), {
            onSuccess: () => queryClient.invalidateQueries(['postLike', { post_id: id }])
        }
    )

    const handleLike = () => {
        liked ? deleteLikeMutation.mutate() : likeMutation.mutate();
    }

    return (
        <div className="px-2 flex flex-row items-center justify-between  h-14">
            <CommentButton comments={isFetchedComments ? comments.length : ''} onClick={openPopup} active={false}/>
            <LikeButton likes={isFetchedLikes ? likes.length : ''} onClick={() => handleLike()} active={liked}/>
            <ShareButton active={false}/>
            <SaveButton active={false}/>
        </div>
    )
}

export default PostActions;
