import axios from 'axios';
import { useQuery, useQueryClient } from '@tanstack/react-query';

const url = 'http://localhost:5000';



export const useUser = (id) => {
    return useQuery(['postUser'], async () => {
        const res = await axios.get(url + '/user/' + id)
        return res.data[0];
    })
}

export const useLikes = (id) => {
    return useQuery(['postLikes'], { post_id: id }, async () => {
        const res = await axios.get(url + '/likes', {
            params: {
                post_id: id
            }
        });
        return res.data;    
    })
}

export const useComments = (id) => {
    return useQuery(['postComments'], { post_id: id }, async () => {
        const res = await axios.get(url + '/comments', {
            params: {
                post_id: id
            }
        }) 
        return res.data;
    })
}

export const useDeletePost = (id) => {
    const queryClient = useQueryClient();
    return useMutation(async () => 
        await request.delete('/posts', {
            data: { id }
        }), {
            onSuccess: () => {
                queryClient.invalidateQueries(['posts']);
                queryClient.invalidateQueries(['profilePosts']);
            }
        }
    )
}

export const useCreateLike = ({ post_id, user_id }) => {
    const queryClient = useQueryClient();
    return useMutation(async () => 
        await request.post('/likes', {
            post_id,
            user_id
        }), {
            onSuccess: () => 
                queryClient.invalidateQueries(['postLike', { post_id: id }])
        }
    )
}

export const useDeleteLike = ({ post_id, user_id }) => {
    const queryClient = useQueryClient();
    return useMutation(async () => 
        await request.delete('/likes', {
            data: {
                post_id,
                user_id
        }}), {
            onSuccess: () => 
                queryClient.invalidateQueries(['postLike', { post_id: id }])
        }
    )
}
