import { useQuery } from "@tanstack/react-query";
import moment from "moment";
import 'moment/locale/pt-br';
import { useContext, useEffect } from "react";
import { useMatch } from "react-router-dom";
import { AuthContext } from "../../contexts/authContext";
import { getComments, getPostById } from "../../services/postsService";
import { baseURL } from "../../utils/constants";
import WriteComment from "../write/writeComment";
import { getPostThread } from '../../utils/getPostThread'
import Post from "./post";

const ViewPost = () => {
    const match = useMatch("/post/:id");
    const id = match.params.id;
    const { currentUser } = useContext(AuthContext);

    const {
        data: postThread,
        isFetched: isFetchedThread
    } = useQuery(['postThread', { id }], () => getPostThread(id));

    useEffect(()=> {
        if(isFetchedThread) console.log(postThread)
    }, [isFetchedThread])

    const { 
        data: postObj, 
        isFetched: isFetchedPost 
    } = useQuery(['post', { id }], () => 
        getPostById(id)
    )

    const { 
        data: parentObj, 
        isFetched: isFetchedParent 
    } = useQuery(['post', { id: postObj?.parent_id }], () => 
        getPostById(postObj.parent_id),
         {
            enabled: !!postObj?.parent_id
        })

    const {
        data: comments,
        isFetched: isFetchedComments
    } = useQuery(['postComments', { parent_id: postObj?.id }], () => 
        getComments(postObj.id),
        {
            enabled: isFetchedPost
        }
    )

    return (
        <div> 
            <div className='sticky top-0 border-b border-stone-700 pt-2 pb-4 pl-3 font-medium text-xl z-20 bg-stone-800'>
                <p>Fweet</p>
            </div> 
            {parentObj && isFetchedParent && <Post postObj={parentObj}/>}
            {isFetchedPost && <Post postObj={postObj}/>}

                {isFetchedPost && <WriteComment parent_id={postObj.id}/>}
            {isFetchedComments && isFetchedPost && comments.map(comment => 
            <div key={comment.id}>
                <Post postObj={comment}/>
            </div>)
            }
        </div>
    )
}

export default ViewPost;
