import moment from "moment";
import { useContext } from "react";
import { AuthContext } from "../../contexts/authContext";
import { request } from "../../utils/axios";

export const useCreatePost = ({ parentId = '' }) => {
    const { currentUser } = useContext(AuthContext);

    const uploadPostImg = async (file) => {
        const formData = new FormData();
        formData.append("file", file);
        const res = await request.post('/upload/post/', formData);
        return res.data;
    }

    const createPost = async (data) => {
        const { title, description, ingredients, post_img } = data;
        const fileUrl = post_img && post_img[0] ? 
            await uploadPostImg(post_img[0]) : '';
        const postObj = {
            user_id: currentUser.id,
            parent_id: parentId,
            title,
            description,
            ingredients: JSON.stringify(ingredients),
            date: moment().format("YYYY-MM-DD HH:mm:ss"),
            file: fileUrl
        }
        return await request.post('/single/', postObj);
    }

    const createMutation = useMutation(
        data => createPost(data), 
        {
            onSuccess: () => {
                queryClient.invalidateQueries(['homePosts']);
                queryClient.invalidateQueries(['profilePosts']);
            }
    });

    return { createPost: data => createMutation.mutate(data) }
}
