import { useContext, useState } from "react";
import { AuthContext } from "../../contexts/authContext";
import { request } from "../../utils/axios";

export const useUpdateUserMutation = () => {
    const { currentUser, setCurrentUser } = useContext(AuthContext);

    const uploadProfileImage = async (file) => {
        const formData = new FormData();
        formData.append("file", file);
        const res = await request.post('/upload/userProfile', formData);
        return res.data;
    }
    
    const uploadBackgroundImage = async (file) => {
        const formData = new FormData();
        formData.append("file", file);
        const res = await request.post('/upload/userBackground', formData);
        return res.data;
    }
    
    const updateUser = async data => {
        const { name, bio, profile_img, profile_bg_img } = data;
        const profileImageUrl = profile_img[0] ? 
            await uploadProfileImage(profile_img[0]) 
            : 
            currentUser.profile_img;

        const backgroundImageUrl = profile_bg_img[0] ? 
            await uploadBackgroundImage(profile_bg_img[0]) 
            : 
            currentUser.profile_bg_img;
    
        const user = {
            name,
            username: currentUser.username,
            bio,
            id: currentUser.id,
            profile_img: profileImageUrl,
            profile_bg_img: backgroundImageUrl
        };
        
        return request.post('/user', user).then(() => user);
    }
    
    const updateUserMutation = useMutation(
        (data) => updateUser(data).then((user) => {
            setCurrentUser(user);
        }), {
                onSuccess: () => {
                    queryClient.invalidateQueries(['profileUser', { 
                        username: currentUser.username
                    }]);
                    queryClient.invalidateQueries(['postUser', { 
                        user_id: currentUser.id 
                    }]);
                }
        }
    )

    return { updateUserMutation };
}
