import { useEffect, useState } from "react";
import { useLocation } from "react-router-dom";
import Popup from "reactjs-popup";
import MainHeader from '../../components/mainHeader';
import UserProfile from '../../components/userProfile';
import Post from "../post/post";
import EditProfileForm from "./editProfileForm";
import { useMutateFollow } from "./useMutateFollow";
import { useGetUserByUsername } from "./useGetUserByUsername";
import { useGetUserPostsById } from "./useGetUserPostsById";
import { useUpdateUser } from "./useUpdateUser";
import { useIsFollowed } from "./useIsFollowed";

const overlayStyle = {
    'background': 'rgba(255,255,255,0.1)',
    'display': 'flex',
    'justifyItems': 'center',
    'alignItems': 'center',
};

const ProfilePage = () => {
    const location = useLocation();
    const username = location.pathname.slice(1);
    const [openModal, setOpenModal] = useState(false);
    const { user, isFetchingUser } = useGetUserByUsername(username);
    const { follow, unfollow } = useMutateFollow(user || '');
    const isFollowed = useIsFollowed(user || '');
    const { posts, isFetchingPosts } = useGetUserPostsById(user || '');
    const { updateUser } = useUpdateUser({
        onUpdateSucess: () => setOpenModal(false) 
    });
    
    const handleFollow = () => {
        isFollowed ? unfollow() : follow()
    }

    useEffect(() => {
        window.scroll(0, 0);
    }, [])

    if(isFetchingUser || isFetchingPosts) return <></>

    return ( 
        <> 
            <MainHeader>
                <p>{user.name}</p>
            </MainHeader>
            <UserProfile 
                user={user} 
                isFollowed={isFollowed} 
                handleFollow={handleFollow}
                onOpenModal={() => setOpenModal(true)}
                />           
            <Popup
                open={openModal}
                onOpen={() => setOpenModal(true)}
                onClose={() => setOpenModal(false)}
                {...{overlayStyle}}
                >   
                <EditProfileForm 
                    onSubmit={(data) => updateUser(data).then(() => {
                        setOpenModal(false)
                    })
                    }
                    onCloseModal={() => setOpenModal(false)}
                    />
            </Popup>
            {posts.map(post => 
                <div 
                    className='border-b border-stone-700'
                    key={post.id}
                    >
                    <Post post={post}/>
                </div>
            )}
        </>
    )
}

export default ProfilePage;
