import { useQuery } from "@tanstack/react-query";
import { request } from "../../utils/axios";

export const useGetUserQuery = ({ id, enabled=true } = {}) => 
    useQuery(['user', { id }], () =>
        request.get('/user', {
            params: { id }
        }).then(res => res.data[0]), {
            enabled: enabled
        }
    );
