import moment from "moment";
import { useEffect, useState } from "react";
import { request } from "../../utils/axios";

export const useGetPostThread = async id => {

    const fetchPost = id => 
        request.get('/single', {
            params: { id }
            }).then(res => {
                const post = res.data[0];
                return {
                    ...post, 
                    ingredients: JSON.parse(post.ingredients),
                    date: moment(post.date).format('YYYY-MM-DD HH:mm:ss')
                }
            })

    const posts = [];
    let post = await fetchPost(id);
    while(post.parent_id) {
        let post = await fetchPost(post.parent_id);
        posts.unshift(post);
    }
    return posts;
}
