import { useMutation, useQuery, useQueryClient } from '@tanstack/react-query';
import moment from 'moment';
import 'moment/locale/pt-br';
import { useContext, useState } from "react";
import { useNavigate } from 'react-router-dom';
import Popup from 'reactjs-popup';
import { AuthContext } from '../../contexts/authContext';
import { deletePost } from '../../services/postsService';
import { getUserById } from '../../services/userService';
import { baseURL } from '../../utils/constants';
import PostActions from "./postActions";
import PostHeader from "./postHeader";
import WriteComment from "./writeComment";

const overlayStyle= {
    'background': 'rgba(255,255,255,0.1)',
    'display': 'flex',
    'justifyItems': 'center',
    'alignItems': 'center',
};

const Post = ({ postObj }) => {
    const [commentPopup, setCommentPopup] = useState(false);
    const { currentUser } = useContext(AuthContext);
    const queryClient = useQueryClient();
    const navigate = useNavigate();
    
    const { 
        id,
        user_id, 
        ingredients,
        description,
        date, 
        title, 
        file
    } = postObj;

    const { data: user, isFetched: isFetchedUser } = useQuery(
        ['postUser', { user_id }], () => getUserById(user_id)
    );

    const deleteMutation = useMutation(
        () => deletePost(id), {
            onSuccess: () => {
                queryClient.invalidateQueries(['posts']);
                queryClient.invalidateQueries(['profilePosts']);
            }
        }
    )

    const handleUserClick = (e) => {
        e.stopPropagation();
        navigate('/' + user.username)
    }

    const renderPostBody = () => (
        <div className="flex flex-col">
            {ingredients && <div className="flex flex-col">
                <p className="font-medium mt-1">{'Ingredientes: '}</p>
                {ingredients.map((ingredient, index) => (
                    <p key={index} className="inline">  {
                            ingredient.quantity + " " + 
                            ingredient.unity + " " + 
                            ingredient.name
                        }
                    </p>
                ))}
            </div>}
            {description && <div>
                <p className="font-medium mt-2">{'Descrição: '}</p>
                <p>{description}</p>
            </div>}
            {file && <img 
                className="rounded-xl max-h-[500px] mt-4" 
                src={baseURL + '/upload/post/' + file} 
                alt=''
            />}
        </div>
    )

    

    return (
        <div 
            onClick={() => navigate('/post/' + postObj.id)} 
            className='hover:backdrop-brightness-110 hover:cursor-pointer border-b border-stone-700' key={id}
            >
            <div className="flex flex-row pt-2 pr-3" key={id}>
            <div className="w-20 flex justify-center">  
                {isFetchedUser && <img 
                    onClick={(e) => handleUserClick(e)}
                    className='w-12 h-12 rounded-[40px] hover:cursor-pointer' 
                    src={baseURL + '/upload/user/' + user.profile_img} 
                    alt=''
                    />}
            </div>
            <div className="w-full">
                <div onClick={(e) => handleUserClick(e)} className="flex justify-between pr-5 ">
                    <div className="flex gap-2 items-center">
                        {isFetchedUser && <p className="font-bold hover:underline hover:cursor-pointer">
                            {user.name}
                        </p>}
                        <div  className="hover:cursor-pointer flex flex-row text-sm text-stone-400 gap-1">
                            {isFetchedUser && <p>{'@' + user.username}</p>}
                            <p> 
                                {"· " + moment(date, 'YYYY-MM-DD HH:mm:ss')
                                .fromNow(true)}
                            </p>
                        </div>
                    </div>
                    {isFetchedUser && user.id === currentUser.id && 
                        <button
                            onClick={() => deleteMutation.mutate()}
                            >
                            ...
                        </button>
                    } 
                </div>
                <p className="text-xl my-1">{title}</p>
                {renderPostBody()} 
                <PostActions post_id={id} onClickComment={() => setCommentPopup(true)}/>
            </div>
        </div>
            <Popup
                open={commentPopup}
                onOpen={() => setCommentPopup(true)}
                onClose={() => setCommentPopup(false)}
                {...{overlayStyle}}
                >   
                <div className='py-6 bg-stone-900 rounded-xl'>
                    <p className="ml-4 mb-4 text-sm">{isFetchedUser ? 'Respondendo à ' + user.name : ''}</p>
                    <PostHeader postObj={postObj}/>
                    <WriteComment closePopup={() => setCommentPopup(false)} postObj={postObj}/>
                </div>
            </Popup>
        </div>
    )
}

export default Post;
