import moment from "moment";
import { useEffect, useState } from "react";
import { request } from "../../utils/axios";

export const useGetPostThread = (id) => {
    const [posts, setPosts] = useState([]);
    const [isFetching, setIsFetching] = useState(true);

    const fetchPost = async id => 
        request.get('/single', {
            params: { id }
            }).then(res => {
                const post = res.data[0];
                return {
                    ...post, 
                    ingredients: JSON.parse(post.ingredients),
                    date: moment(post.date).format('YYYY-MM-DD HH:mm:ss')
                }
            })

    useEffect(() => {
        console.log(posts[posts.length - 1])
        if(posts.length === 0) {
            const post = fetchPost(id);
            setPosts([post]);
        }
        else if(posts[posts.length - 1].parent_id) {
            const post = fetchPost(posts[posts.length - 1].parent_id);
            const newPosts = [...posts];
            newPosts.unshift(post);
            setPosts(newPosts);
        }
    }, [posts])

    return { posts, isFetching }

}


