import { useQuery } from "@tanstack/react-query"
import { useContext } from "react"
import { AuthContext } from "../../contexts/authContext"

export const useGetHomePosts = () => {
    const { currentUser } = useContext(AuthContext);

    const { data, isFetching } = useQuery(['homePosts'], () =>
        request.get('/posts/', {
            params: { id: currentUser.id }
        }).then(res => res.data.map(post => ({
            ...post, 
            ingredients: JSON.parse(post.ingredients),
            date: moment(post.date).format('YYYY-MM-DD HH:mm:ss')
        })
    )))

    return { posts: data, isFetchingPosts: isFetching }
}
