import { useMutation } from "@tanstack/react-query";
import moment from "moment";
import { useContext } from "react";
import { useForm } from "react-hook-form";
import { Link } from "react-router-dom";
import PostButton from "../../components/postButton";
import { AuthContext } from "../../contexts/authContext";
import { baseURL } from "../../utils/constants";
import { createComment } from "../../services/commentService";
import PostHeader from "./postHeader";

const WriteComment = ({ postObj, user, closePopup }) => {
    const { handleSubmit, register } = useForm();
    const { currentUser } = useContext(AuthContext);

    const onSubmit = (data) => {
        commentMutation.mutate(data);
    }

    const commentMutation = useMutation(
        (data) => createComment({ 
            user_id: currentUser.id,
            post_id: postObj.id,
            comment_text: data.comment_text            
         }), {
            onSuccess: () => {
                closePopup();
            }
         }
    );

    return(
        <form autoComplete="off" className="gap-2 py-10 flex flex-col w-[600px] bg-stone-900 rounded-xl pb-14" onSubmit={handleSubmit(onSubmit)}>
            <p className="ml-4 mb-4 text-sm">{'Respondendo à ' + user.name}</p>
            <PostHeader postObj={postObj}/>
            <div className="flex flex-row">
                <div className='flex flex-col min-w-fit mx-3'>
                    <img src={baseURL + '/upload/user/' + currentUser.profile_img} alt='' className='w-12 h-12 rounded-[40px]'/>
                </div>
                <input 
                    placeholder={'Digite aqui ...'}
                    className={`placeholder:text-stone-500 rounded-md text-white align-middle outline-none h-14 bg-inherit text-xl py-1 pl-2 focus:placeholder:invisible focus:border-stone-500 hover:border-stone-600 transition-colors`}
                    {...register('comment_text')}
                    />
            </div>
            <div className="flex justify-end">
                <div className="mr-4 w-28">
                    <PostButton isValid={true} type='submit'/>
                </div>
            </div>
        </form>
    )
}

export default WriteComment;
