import { useQuery } from "@tanstack/react-query";
import { request } from "../../utils/axios";

export const useGetCommentsQuery = ({ id, enabled=true } = {}) => 
    useQuery(['comments'], () => 
        request.get('/comments', {
            params: {
                parent_id: id,
            }
        }).then(res => res.data), {
                enabled: enabled
            }
        )
