import { createSlice } from '@reduxjs/toolkit';
import { request } from '../../utils/axios';

export const postSlice = createSlice({
    name: 'post',
    initialState: [],
    reducers: {
        fetchHomePagePosts: state => {
            state.homePagePosts = async ({ id }) => 
                request.get('/posts/', {
                    params: { id }
                }).then(res => res.data.map(post => ({
                        ...post, 
                        ingredients: JSON.parse(post.ingredients),
                        date: moment(post.date).format('YYYY-MM-DD HH:mm:ss')
                })
            ))
        }
    },
})

export const { fetchHomePagePosts } = postSlice.actions;

export default postSlice.reducer;
