import axios from 'axios';
import { useMutation, useQuery, useQueryClient } from '@tanstack/react-query';

const url = 'http://localhost:5000';

export const useUser = (id) => {
    return useQuery(['postUser'], async () => {
        const res = await axios.get(url + '/user/', {
            params:{ id }
        })
        return res.data[0];
    })
}

export const useLikes = (id) => {
    return useQuery(['postLikes'], { post_id: id }, async () => {
        const res = await axios.get(url + '/likes', {
            params: {
                post_id: id
            }
        });
        return res.data;    
    })
}

export const useComments = (id) => {
    return useQuery(['postComments'], { post_id: id }, async () => {
        const res = await axios.get(url + '/comments', {
            params: {
                post_id: id
            }
        }) 
        return res.data;
    })
}

export const useDeletePost = (id) => {
    const queryClient = useQueryClient();
    return useMutation(async () => 
        await axios.delete(url + '/posts', {
            data: { id }
        }), {
            onSuccess: () => {
                queryClient.invalidateQueries(['posts']);
                queryClient.invalidateQueries(['profilePosts']);
            }
        }
    )
}

export const useCreateLike = ({ post_id, user_id }) => {
    const queryClient = useQueryClient();
    return useMutation(async () => 
        await axios.post(url + '/likes', {
            post_id,
            user_id
        }), {
            onSuccess: () => 
                queryClient.invalidateQueries(['postLike', { post_id }])
        }
    )
}

export const useDeleteLike = ({ post_id, user_id }) => {
    const queryClient = useQueryClient();
    return useMutation(async () => 
        await axios.delete(url + '/likes', {
            data: {
                post_id,
                user_id
        }}), {
            onSuccess: () => 
                queryClient.invalidateQueries(['postLike', { post_id }])
        }
    )
}

export const useCreatePost = async ({ postData, user_id }) => {
    const queryClient = useQueryClient();
    return useMutation(async () => {
        const { title, description, ingredients, file } = postData;
        const fileUrl = file[0] ? await uploadImg(file[0]) : '';
        const postObj = {
            user_id,
            title,
            description,
            ingredients: JSON.stringify(ingredients),
            date: moment().format("YYYY-MM-DD HH:mm:ss"),
            file: fileUrl
        }
        return await request.post('/posts/', postObj);
    }, {
        onSuccess: () => queryClient.invalidateQueries(['posts'])
    })
}
