import moment from "moment";
import { useEffect, useState } from "react";
import { request } from "../../utils/axios";

export const useGetPostThread = ({ parent_id = '' } = {}) => {
    const [posts, setPosts] = useState([]);
    const [isFetching, setIsFetching] = useState(false);

    const fetchPost = async id => {
        setIsFetching(true);
        const post = await request.get('/single', {
            params: { id }
            }).then(res => {
                const post = res.data[0];
                return {
                    ...post, 
                    ingredients: JSON.parse(post.ingredients),
                    date: moment(post.date).format('YYYY-MM-DD HH:mm:ss')
                }
            })
            setPosts(posts => [...posts, post]);
            setIsFetching(false);
        }
        
    useEffect(() => {
        if(posts.length === 0 && parent_id) {
            fetchPost(parent_id);
        }
        else if(posts[posts.length - 1]?.parent_id) {
            fetchPost(posts[posts.length - 1].parent_id)
        }
    }, [posts, parent_id])
    
    return { posts, isFetching }
}
