import { LikeIcon } from "../../icons/likeIcon";

const LikeButton = ({ active, color, likes, onClick }) => {
    return(
        <button 
            onClick={onClick} 
            className={`flex justify-center items-center gap-2 p-2 
                    hover:bg-${color} hover:bg-opacity-10 
                    [&:hover_path]:stroke-${color} transition rounded-full`}
            >
            <LikeIcon color={color} active={active}/>
            {likes}
        </button>
    )
}

export default LikeButton;
