import { useContext } from "react";
import { AuthContext } from '../../contexts/authContext';
import Input from '../../components/input';
import { useNavigate } from "react-router-dom";
import { Link } from "react-router-dom";
import { useForm } from "react-hook-form";

const LoginPage = () => {
    const { loginUser } = useContext(AuthContext);
    const { register, handleSubmit } = useForm();
    const navigate = useNavigate();

    const onSubmit = async (data) => {
        const { username, password } = data;
        loginUser(username, password).then(() =>
            navigate('/')
        );
    }

    return(
        <div className="bg-stone-800 grid 
                        justify-center items-center min-h-screen">
            <form 
                autoComplete="false"
                onSubmit={handleSubmit(onSubmit)} 
                className="box-content flex flex-col w-96 
                            pt-11 pb-24 px-24 bg-stone-900 rounded-lg">
                <h1 className="text-3xl font-sans font-medium antialiased 
                            tracking-tight text-white">
                    Entrar no fwitter
                </h1>
                <div className='flex flex-col gap-3 mt-10'>
                    <Input
                        placeholder='Nome de usuário'
                        name='username'
                        register={register}
                        border={true}
                        />
                    <Input
                        placeholder='Senha'
                        name='password'
                        register={register}
                        border={true}
                        />
                </div>
                <div>
                    <button className="text-black mt-6 bg-gray-50 
                            hover:bg-gray-100 active:bg-gray-200 rounded-2xl 
                            font-bold w-full h-9">
                        Avançar
                    </button>
                </div>
                <div className="font-sm mt-16 flex text-gray-100 gap-1">
                    <p>Ainda não tem uma conta?</p>
                    <Link to='/register'>
                        <p className="text-stone-500 hover:underline">
                            Inscrever-se
                        </p>
                    </Link>
                </div>
            </form>
        </div>
    )
}

export default LoginPage;
