import { request } from "../../utils/axios"

export const getComments = async (post_id) => {
    const res = await request.get('/comments', {
        params: {
            post_id,
        }
    })
    return res.data;
}

export const createComment = async ({ post_id, user_id, comment_text }) => 
    await request.post('/comments', {
        post_id,
        user_id,
        comment_text
    })

export const deleteComment = async (post_id) => 
    await request.delete('/comments', {
        data: {
            post_id
    }})
