import { useMutation, useQueryClient } from "@tanstack/react-query";
import { useContext } from "react";
import { useForm } from "react-hook-form";
import PostButton from "../../components/postButton";
import { AuthContext } from "../../contexts/authContext";
import { createComment } from "../../services/commentService";
import { baseURL } from "../../utils/constants";

const WriteComment = ({ postObj, closePopup }) => {
    const { handleSubmit, register } = useForm();
    const { currentUser } = useContext(AuthContext);
    const queryClient = useQueryClient();

    const onSubmit = (data) => {
        commentMutation.mutate(data);
    }

    const commentMutation = useMutation(
        (data) => createComment({ 
            user_id: currentUser.id,
            post_id: postObj.id,
            comment_text: data.comment_text            
         }), {
            onSuccess: () => {
                queryClient.invalidateQueries(['post', { post_id: postObj.id }])
                closePopup && closePopup();
            }
         }
    );

    return(
        <form autoComplete="off" className="flex flex-col bg-inherit" onSubmit={handleSubmit(onSubmit)}>
                <input 
                    placeholder={'Digite sua resposta ...'}
                    className={`placeholder:text-stone-500 rounded-md text-white align-middle outline-none h-14 bg-inherit text-xl pl-2 focus:placeholder:invisible focus:border-stone-500 hover:border-stone-600 transition-colors`}
                    {...register('comment_text')}
                    />
            <div className="flex justify-end">
                <div className="mr-4 w-28">
                    <PostButton isValid={true} type='submit'/>
                </div>
            </div>
        </form>
    )
}

export default WriteComment;
