import { useQuery } from '@tanstack/react-query';
import { useContext, useEffect } from 'react';
import { useDispatch, useSelector } from 'react-redux'
import { useNavigate } from 'react-router-dom';
import Post from '../containers/post/post';
import { setHomePagePosts } from '../containers/post/postSlice';
import { AuthContext } from '../contexts/authContext';
import { getPostsById } from '../services/postsService';

const HomePage = () => {
    const { currentUser } = useContext(AuthContext);
    const dispatch = useDispatch();
    const { isLoading } = useQuery(['homePagePosts'], () =>
        getPostsById(currentUser.id), {
            onSuccess: data => {
                dispatch(setHomePagePosts(data))
            }
        }
    )
    const posts = useSelector((state) => state.post.homePagePosts);
    const navigate = useNavigate();

    const handlePostClick = (post) => {
        navigate('/post/' + post.id)
    }


    useEffect(() => {
        if(!currentUser) navigate('/login');
        console.log(posts)
    }, [posts])

    return (
        <>
            <div className='sticky top-0 pt-2 pb-4 pl-3 font-medium text-xl 
                        z-20 bg-stone-800 border-b border-stone-700'>
                <p>Inicio</p>
            </div>  
            <div className="flex flex-col"> 
            <div className='border-b border-stone-700'>
            </div>
            {posts && posts.map(post => 
                <div 
                    onClick={() => handlePostClick(post)} 
                    className='border-b border-stone-700'
                    key={post.id}
                    >
                    <Post postObj={post}/>
                </div>
            )}
            </div>
        </>
    )
}

export default HomePage;
