import { useQuery } from "@tanstack/react-query";
import moment from "moment";
import { request } from "../../utils/axios";

export const useGetUserPosts = (user) => {
    const { 
        data, 
        isFetching 
    } = useQuery(['profilePosts', { user_id: user?.id }], () => 
        request.get('/posts/', {
            params: { id: user.id }
        }).then(res => res.data.map(post => ({
            ...post, 
            ingredients: JSON.parse(post.ingredients),
            date: moment(post.date).format('YYYY-MM-DD HH:mm:ss')
        }))), {
            enabled: !!user
        }
    );

    return { posts: data, isFetchingPosts: isFetching }
}
