import { useMutation } from "@tanstack/react-query";
import { useContext } from "react";
import { useForm } from "react-hook-form";
import Input from "../../components/input";
import { AuthContext } from "../../contexts/authContext";
import { createComment } from "./commentService";

const Comment = ({ post, user, closePopup }) => {
    const { handleSubmit, register, reset } = useForm();
    const { currentUser } = useContext(AuthContext);

    const onSubmit = (data) => {
        commentMutation.mutate(data).then(() => closePopup());
    }

    const commentMutation = useMutation(
        (data) => createComment({ 
            user_id: currentUser.id,
            post_id: post.id,
            comment_text: data.comment_text            
         }), {
            onSuccess: () => console.log('success')
         }
    );

    return(
        <form autoComplete="off" className="py-10 flex flex-col w-[600px] bg-stone-900 rounded-xl pb-14" onSubmit={handleSubmit(onSubmit)}>
            <p className="ml-4 mb-4 text-sm">{'Respondendo à ' + user.name}</p>
            <div className="flex flex-row">
                <div className='min-w-fit mx-3'>
                    <img src={`http://localhost:5000/upload/user/${currentUser.profile_img}`} alt='' className='w-12 h-12 rounded-[40px]'/>
                </div>
                <Input
                    register={register}
                    name='comment_text'
                    border={false}
                    placeholder={'Digite seu comentário'}
                    />
            </div>
            <button type='submit'>Enviar comentário</button>
        </form>
    )
}

export default Comment;
