import moment from "moment";
import { useEffect, useState } from "react";
import { request } from "../../utils/axios";

export const useGetPostThread = ({ parent_id = '' } = {}) => {
    const [posts, setPosts] = useState([]);
    const [isFetching, setIsFetching] = useState(true);

    const addPost = async id => {
        const post = await request.get('/single', {
            params: { id }
            }).then(res => {
                const post = res.data[0];
                return {
                    ...post, 
                    ingredients: JSON.parse(post.ingredients),
                    date: moment(post.date).format('YYYY-MM-DD HH:mm:ss')
                }
            })
        const newPosts = [...posts];
        newPosts.unshift(post);
        setPosts(newPosts);
    }

    useEffect(() => {
        console.log(posts[posts.length - 1])
        if(posts.length === 0 && parent_id) {
            addPost(parent_id);
        }
        else if(posts[posts.length - 1].parent_id) {
            addPost(posts[posts.length - 1].parent_id)
        }
    }, [posts])

    return { posts, isFetching }

}


