import { useMutation, useQuery, useQueryClient } from "@tanstack/react-query";
import { request } from "../../utils/axios";

export const useQueryPosts = (postObj) => {
    const queryClient = useQueryClient();

    const {
        id,
        user_id,
        parent_id,
        share_user_id    
    } = postObj;

    const { data: postUser, isFetched: isFetchedPostUser } = useQuery(
        ['postUser', { id }], () => 
            request.get('/user', {
                params:{ id: user_id }
            }).then(res => res.data[0])
    );

    const { data: shareUser, isFetched: isFetchedShare } = useQuery(
    ['postShareUser', { id }], () => 
            request.get('/user', {
                params:{ id: share_user_id }
            }).then(res => res.data[0]) ,{
                enabled: !!share_user_id
            }
    );

    const { data: parentUser, isFetched: isFetchedParentUser} = useQuery(
        ['postParentUser', { id }], () => 
        request.get('/posts/', {
            params: { id: parent_id }
        }).then(post => 
            request.get('/user', {
                params: { id: post.user_id }
            }).then(res => res.data[0]), 
        {
            enabled: !!parent_id
        }
    ))

    const deleteMutation = useMutation(
        request.delete('/single/', {
            data: { id }
        }), {
            onSuccess: () => {
                queryClient.invalidateQueries(['posts']);
                queryClient.invalidateQueries(['profilePosts']);
                queryClient.invalidateQueries(['postComments', { parent_id }]);
            }
        }
    )

    const isFetched = isFetchedParentUser && isFetchedPostUser && isFetchedShare

    return { 
        postUser, 
        shareUser, 
        parentUser, 
        isFetched,
        deletePost: deleteMutation.mutate() 
    }

}
