import { useEffect, useState } from "react";
import { useGetPostByIdQuery } from "../../hooks/useGetPostByIdQuery";

export const useGetPostThread = async id => {
    const [posts, setPosts] = useState([]);
    const { data: post } = useGetPostByIdQuery({ id });


    useEffect(() => {
        if(posts.length == 0 && post.parent_id) {
            const parentPost = useGetPostByIdQuery({ id: post.parent_id });
            setPosts(posts => [...posts, parentPost]);
        }
        else {
            const parentPost = posts[posts.length - 1];
            if(parentPost.parent_id) {
                const { data: parentPost } = useGetPostByIdQuery({ id });
                setPosts(posts => [...posts, parentPost]);
            }
        }

    }, [posts])

    return posts;
}
