import { useMutation, useQuery, useQueryClient } from "@tanstack/react-query";
import { baseURL } from '../../utils/constants';
import moment from "moment";
import 'moment/locale/pt-br';
import { useContext, useEffect, useState } from "react";
import { Link } from "react-router-dom";
import { AuthContext } from "../../contexts/authContext";
import { createLike, deleteLike, deletePost, getLikes } from "./postService";
import { getUserById } from "../../services/userService";
import CommentButton from "./commentButton";
import LikeButton from "./likeButton";
import SaveButton from "./saveButton";
import ShareButton from "./shareButton";

const Post = ({ postObj }) => {
    const { currentUser } = useContext(AuthContext);
    const queryClient = useQueryClient();
    const [liked, setLiked] = useState(false);
    
    const { 
        id,
        user_id, 
        date, 
        title, 
        ingredients, 
        description, 
        file,
    } = postObj;

    const { data: user } = useQuery(
        ['postUser', { user_id }], () => getUserById(user_id)
    );

    const { data: likes, isFetching: isFetchingLikes } = useQuery(
        ['postLike', { post_id: id }], () => getLikes(id)
        )

    useEffect(() => {
        if(likes) {
            setLiked(likes.filter(like => like.user_id === currentUser.id).length ? true : false)
        }
    }, [likes])

    const deleteMutation = useMutation(
        () => deletePost(id), {
            onSuccess: () => {
                queryClient.invalidateQueries(['posts']);
                queryClient.invalidateQueries(['profilePosts']);
            }
        }
    )

    const likeMutation = useMutation(
        () => createLike({ post_id: id, user_id: currentUser.id }), {
            onSuccess: () => queryClient.invalidateQueries(['postLike', { post_id: id }])
        }
    )

    const dislikeMutation = useMutation(
        () => deleteLike({ post_id: id, user_id: currentUser.id }), {
            onSuccess: () => queryClient.invalidateQueries(['postLike', { post_id: id }])
        }
    )

    const handleLike = () => {
        liked ? dislikeMutation.mutate() : likeMutation.mutate();
    }

    return (
        <div className="flex flex-row pt-2 pr-3 border-b border-stone-700" key={id}>
            <div className="w-20 flex justify-center">  
                {user && 
                    <img 
                        className='w-12 h-12 rounded-[40px]' 
                        src={baseURL + '/upload/user/' + user.profile_img} 
                        alt=''
                        />
                }
            </div>
            <div className="w-full">
                <div className="flex justify-between pr-5">
                    <div className="flex gap-2 items-center">
                        {user && 
                            <Link 
                                to={'/' + user.username} 
                                className="font-bold"
                                >
                                {user.name}
                            </Link>
                        }
                        <div className="flex flex-row text-sm text-stone-400 gap-1">
                            <p>{user && '@' + user.username}</p>
                            <p> {
                                    "· " + 
                                    moment(date, 'YYYY-MM-DD HH:mm:ss')
                                    .fromNow(true)
                            }
                            </p>
                        </div>
                    </div>
                    {user && user.id === currentUser.id && 
                        <button
                            onClick={() => deleteMutation.mutate()}
                            >
                            ...
                        </button>
                    } 
                </div>
                <p className="text-xl my-1">{title}</p>
                {ingredients && ingredients.length > 0 && 
                    <div className="flex flex-col">
                        <p className="font-medium mt-1">{'Ingredientes: '}</p>
                        {ingredients.map((ingredient, index) => (
                            <p key={index} className="inline">  {
                                    ingredient.quantity + " " + 
                                    ingredient.unity + " " + 
                                    ingredient.name
                                }
                            </p>
                        ))}
                    </div>
                }
                {description && 
                    <div>
                        <p className="font-medium mt-2">{'Descrição: '}</p>
                        <p>{description}</p>
                    </div>
                }
                {file && <img className="rounded-xl max-h-[500px] mt-4" src={baseURL + '/upload/post/' + file} alt=''/>}
                <div className="px-2 flex flex-row items-center justify-between  h-14">
                    <CommentButton active={false}/>
                    <LikeButton label={likes ? likes.length : ''} onClick={() => handleLike()} active={liked}/>
                    <ShareButton active={false}/>
                    <SaveButton active={false}/>
                </div>
            </div>
        </div>
    )
}

export default Post;
