import 'moment/locale/pt-br';
import { useEffect, useRef } from "react";
import { useMatch } from "react-router-dom";
import { useCreatePostMutation } from "../../hooks/useCreatePostMutation";
import { useGetPostByIdQuery } from "../../hooks/useGetPostByIdQuery";
import { useGetPostInteractionQuery } from '../../hooks/useGetPostInteractionQuery';
import CreateCommentForm from "../post/createCommentForm";
import Post from "../post/post";
import { useGetPostThread } from "./fetchPostThread";

const PostPage = () => {
    const match = useMatch("/post/:id");
    const id = match.params.id;
    const postRef = useRef();

    const { 
        data: post, 
        isFetching: isFetchingPost 
    } = useGetPostByIdQuery({ id });

    const createPostMutation  = useCreatePostMutation({ parent_id: id })

    const { posts: thread } = useGetPostThread(post);
    
    const { 
        data: comments, 
        isFetching: isFetchingComments 
    } = useGetPostInteractionQuery({
        post_id: id, queryKey: 'comments', endpoint: '/comments' 
    });
    
    const scroll = () => {
        if(thread && thread.length === 0) window.scroll(0,0);
        else {
            postRef.current.scrollIntoView({ block:'start' });
            window.scrollBy({ top: -50 })
        } 
    }

    const isFetching = isFetchingPost || isFetchingComments
    
    useEffect(() => {
        if(!isFetching) scroll();
    }, [isFetching])

    if(isFetching) return <></>

    return (
        <div> 
            <div className='sticky top-0 border-b border-stone-700 
                            pt-2 pb-4 pl-3 font-medium text-xl 
                            z-20 bg-stone-800'>
                <p>Fweet</p>
            </div> 
            {thread && thread.length > 0 && thread.map(post => 
                <div 
                    className={`hover:backdrop-brightness-110 
                                hover:cursor-pointer`}
                    key={post.id}
                    >
                    <Post post={post} isThread={true} />
                </div>
            )}
            <div ref={postRef} className="[&_>*]:border-b 
                                        [&_>*]:border-stone-700">
                <Post post={post}/>
                <CreateCommentForm onSubmit={
                    (data) => createPostMutation.mutate(data)
                }/>
            </div>
            {comments && comments.map((post, index) => 
                <div 
                    key={post.id}
                    className='hover:backdrop-brightness-110 
                                hover:cursor-pointer border-b border-stone-700'
                    >
                    <Post post={post}/>
                </div>
            )}
        </div>
    )
}

export default PostPage;
