import { useMutation, useQuery, useQueryClient } from '@tanstack/react-query';
import 'moment/locale/pt-br';
import { useContext } from "react";
import { useNavigate } from 'react-router-dom';
import PostBody from '../../components/postBody';
import PostShare from '../../components/postShare';
import PostUserBox from '../../components/postUserBox';
import UserInfo from '../../components/userInfo';
import { AuthContext } from '../../contexts/authContext';
import { deletePost, getPostById } from '../../services/singleService';
import { getUserById } from '../../services/userService';
import PostActions from "./postActions";

const Post = ({ post, type }) => {
    const { currentUser } = useContext(AuthContext);
    const queryClient = useQueryClient();
    const navigate = useNavigate();

    const { 
        id,
        user_id, 
        parent_id,
        date, 
        title, 
        share_user_id
    } = post;

    const { data: user, isFetched: isFetchingUser } = useQuery(
        ['postUser', { id }], () => getUserById(user_id)
    );

    const { data: parentUser, isFetched: isFetchedParentUser} = useQuery(
        ['postResponseUser', { id: parent_id }], () => 
        getPostById(parent_id).then(post => 
            getUserById(post.user_id)), 
        {
            enabled: !!parent_id
        }
    )

    const { data: shareUser, isFetched: isFetchedShare } = useQuery(
        ['postShareUser', { id }], () => getUserById(share_user_id),{
            enabled: !!share_user_id
        }
    );

    const deleteMutation = useMutation(
        () => deletePost(id), {
            onSuccess: () => {
                queryClient.invalidateQueries(['posts']);
                queryClient.invalidateQueries(['profilePosts']);
                queryClient.invalidateQueries(['postComments', { parent_id }]);
            }
        }
    )

    const handleRemove = (e) => {
        e.stopPropagation();
        deleteMutation.mutate();
    }

    const handleUserClick = (e, user) => {
        e.stopPropagation();
        navigate('/' + user.username)
    }

    const handlePostClick = () => {
        navigate('/post/' + id);
    }

    if(isFetchingUser) return <></>

    return (
        <div 
            onClick={() => handlePostClick()} 
            className='flex flex-col pt-2 pr-3 
                hover:backdrop-brightness-110
                hover:cursor-pointer'
            >
            <div className="flex flex-row">
                <div className="w-full">

                    
                    <p className="text-xl my-1">{title}</p>
                    <PostBody post={post}/>
                    <PostActions post={post}/>
                </div>
            </div>
        </div>
    )
}

export default Post;
