import { useMutation, useQueryClient } from '@tanstack/react-query';
import { useContext, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import MainHeader from '../../components/mainHeader';
import { AuthContext } from '../../contexts/authContext';
import { createPost } from '../../services/singleService';
import Post from '../post/post';
import PostForm from '../post/postForm';
import { useHomePostsQuery } from './useHomePostsQuery';

const HomePage = () => {
    const { currentUser } = useContext(AuthContext);
    const queryClient = useQueryClient();
    const navigate = useNavigate();
    const { data: posts, isFetching: isFetchingPosts } = useHomePostsQuery();

    const createMutation = useMutation(
        data => createPost({ data, user_id: currentUser.id, parent_id: null}), 
        {
            onSuccess: () => {
                queryClient.invalidateQueries(['homePosts']);
                queryClient.invalidateQueries(['profilePosts']);
            }
    });
    
    useEffect(() => {
        if(currentUser === null) navigate('/login');
    }, [])
    
    if(isFetchingPosts) return <></>

    return (
        <>
            <MainHeader>
                Inicio
            </MainHeader>
            <PostForm onSubmit={(data) => createMutation.mutate(data)}/>
            {posts.map(post => 
                <div 
                    className='border-b border-stone-700' 
                    key={post.id}
                    >
                    <Post post={post}/>
                </div>
            )}
        </>
    )
}

export default HomePage;
