import moment from "moment";
import { useEffect, useState } from "react";
import { request } from "../../utils/axios";

export const useFetchPostThread = ({ parent_id = '' } = {}) => {
    const [data, setData] = useState([]);
    const [isFetching, setIsFetching] = useState(true);

    const fetchPost = async id => {
        setIsFetching(true);
        const post = await request.get('/single', {
            params: { id }
            }).then(res => {
                const post = res.data[0];
                return {
                    ...post, 
                    ingredients: JSON.parse(post.ingredients),
                    date: moment(post.date).format('YYYY-MM-DD HH:mm:ss')
                }
            })
            setData(posts => [...posts, post]);
            setIsFetching(false);
        }
        
    useEffect(() => {
        if(data.length === 0 && parent_id) {
            fetchPost(parent_id);
        }
        else if(data[data.length - 1]?.parent_id) {
            fetchPost(data[data.length - 1].parent_id)
        }
    }, [data, parent_id])
    
    return { data, isFetching }
}
