import 'moment/locale/pt-br';
import { useEffect } from 'react';
import UserBox from '../../components/userBox';
import PostHeader from '../../components/postHeader';
import { baseURL } from '../../utils/urls';

const Post = ({ post, isThread }) => {

    
    const { 
        id,
        author,
        comments,
        likes,
        shares,
        bookmarks,
        createdAt,
        title,
        description,
        ingredients,
        file
    } = post;
    
    useEffect(() => {
        if(post) console.log(post)
    }, [post])

    const handleUserClick = (e, user) => {
        e.stopPropagation();
        navigate('/' + user.username)
    }

/*     const deleteMutation = useMutation(
        () => deletePost(id), {
            onSuccess: () => {
                queryClient.invalidateQueries(['homePosts']);
                queryClient.invalidateQueries(['profilePosts']);
                queryClient.invalidateQueries(['bookmarkedPosts']);
                queryClient.invalidateQueries(['postComments', { parent_id }]);
            }
        }
    ) */

    return (
        <>
            {/* {shareUser && <PostShare shareUser={shareUser} onClick={handleUserClick}/>} */}
            <div className="flex flex-row">
                <UserBox user={author} isThread={isThread}/>
                <div className="w-full">
                    <div className='flex justify-between'>
                        <PostHeader 
                            user={author} 
                            handleUserClick={handleUserClick} 
                            date={moment(createdAt, 'YYYY-MM-DD HH:mm:ss')
                                    .fromNow(true)
                                }
                            />
                        {/* {user.id === currentUser.id && 
                            <button
                                onClick={(e) => handleRemove(e)}
                                >
                                ...
                            </button>
                        } */}
                    </div>
                    {/* {parentUser && 
                        <div className='text-sm text-stone-400 '>
                            <p className='inline'>{'Respondendo à '}</p>
                            <p onClick={(e) => handleUserClick(e, parentUser)}
                                className='inline hover:cursor-pointer 
                                hover:underline text-stone-200'
                                >
                                @{parentUser.username}
                            </p>
                        </div>
                    } */}
                    <div className="flex flex-col">
                        <p className="text-xl my-1">{title}</p>
                        {ingredients && ingredients.length > 0 && 
                            <div className="flex flex-col">
                                <p className="font-medium mt-1">
                                    {'Ingredientes: '}
                                </p>
                                {ingredients.map((ingredient, index) => (
                                    <p key={index} className="inline">  {
                                            ingredient.quantity + " " + 
                                            ingredient.unity + " " + 
                                            ingredient.name
                                        }
                                    </p>
                                ))}
                            </div>
                        }
                        {description && 
                            <div>
                                <p className="font-medium mt-2">
                                    {'Descrição: '}
                                </p>
                                <p>{description}</p>
                            </div>
                        }
                        {file && 
                            <img 
                                className="rounded-xl max-h-[500px] mt-4" 
                                src={baseURL + '/upload/post/' + file} 
                                alt=''
                            />
                        }
                    </div>
                </div>
            </div>
        </>
    )
}

export default Post;
