import { useQuery } from "@tanstack/react-query";
import moment from "moment";
import { request } from "../../utils/axios";

export const useGetPostThread = ({ id }) => {

    const getPost = id => 
        request.get('/single', {
            params: { id }
            }).then(res => {
                const post = res.data[0];
                return {
                    ...post, 
                    ingredients: JSON.parse(post.ingredients),
                    date: moment(post.date).format('YYYY-MM-DD HH:mm:ss')
                }
            })

    const getPostThread = async id => {
        const posts = [];
        let post = await getPost(id);
        while(post.parent_id) {
            let post = await getPost(post.parent_id);
            posts.unshift(post);
        }
        return posts;
    }
    
    return useQuery(['postThread', { id }], () => 
        getPostThread(id), {
            enabled: !!id
        }); 
}
