import moment from "moment";
import { useNavigate } from "react-router-dom";

const UserInfo = ({ user, postDate }) => { 
    const navigate = useNavigate();

    const handleUserClick = (e, user) => {
        e.stopPropagation();
        navigate('/' + user.username)
    }

    return (
        <div className="flex justify-between">
            <div onClick={(e) => handleUserClick(e, user)} className="flex gap-2 items-center">
                <p className="font-bold hover:underline hover:cursor-pointer">
                    {user.name}
                </p>
                <div className="hover:cursor-pointer flex flex-row text-sm text-stone-400 gap-1">
                    <p>{'@' + user.username}</p>
                    <p> 
                        {"· " + moment(postDate, 'YYYY-MM-DD HH:mm:ss')
                        .fromNow(true)}
                    </p>
                </div>
            </div>
        </div>
    )
}

export default UserInfo;
