import { useMutation, useQueryClient } from '@tanstack/react-query';
import 'moment/locale/pt-br';
import { useContext } from "react";
import { useNavigate } from 'react-router-dom';
import PostBody from '../../components/post/postBody';
import { request } from '../../utils/axios';
import Interactions from "../interactions/interactions";
import { useGetPostByIdQuery } from '../../hooks/useGetPostByIdQuery';
import { AuthContext } from "../../contexts/authContext";
import { useGetUserByIdQuery } from './useGetUserByIdQuery';

const Post = ({ post, isThread }) => {
    const queryClient = useQueryClient();
    const navigate = useNavigate();
    const { currentUser } = useContext(AuthContext);

    const { 
        id,
        user_id, 
        parent_id,
        share_user_id
    } = post

    const { 
        data: user, 
        isFetching: isFetchingUser 
    } = useGetUserByIdQuery({ id: user_id });
    const { 
        data: shareUser 
    } = useGetUserByIdQuery({ id: share_user_id });
    const { 
        data: parentPost 
    } = useGetPostByIdQuery({ id: parent_id });
    const { 
        data: parentPostUser 
    } = useGetUserByIdQuery({ id: parentPost?.user_id });

    const deleteMutation = useMutation(
        () =>  request.delete('/single/', {
            data: { id }
        }), {
            onSuccess: () => {
                queryClient.invalidateQueries(['homePosts']);
                queryClient.invalidateQueries(['profilePosts']);
                queryClient.invalidateQueries(['bookmarkedPosts']);
                if(parent_id) {
                    queryClient.invalidateQueries(['comments'])
            }
        }}
    )

    if(isFetchingUser) return <></>

    return (
        <div 
            onClick={() => navigate('/post/' + id)} 
            className='flex flex-col pt-2 pr-3 
                hover:backdrop-brightness-110
                hover:cursor-pointer'
            >
                <PostBody 
                    post={post}
                    currentUser={currentUser}
                    user={user}
                    shareUser={shareUser}
                    parentPostUser={parentPostUser}
                    isThread={isThread}
                    handleUserClick={(e, user) => {
                        e.stopPropagation();
                        navigate('/' + user.username)
                    }}
                    handleRemove={e => {
                        e.stopPropagation();
                        deleteMutation.mutate();
                    }}
                    >
                    <Interactions post={post} user={user}/>
                </PostBody>
        </div>
    )
}

export default Post;
