import { baseURL } from "../utils/constants";
import UserBox from "./userBox";
import UserInfo from "./userInfo";

const PostBody = (props) => {
    const { 
        title,
        ingredients, 
        description, 
        file, 
        parentUser,
        handleUserClick,
        handleRemove
    } = props;

    return (
        <div className="flex flex-row">
            <UserBox user={user} type={type}/>
            <div className="w-full">
                <div className='flex justify-between'>
                    <UserInfo user={user} date={date}/>
                    {user.id === currentUser.id && 
                        <button
                            onClick={(e) => handleRemove(e)}
                            >
                            ...
                        </button>
                    }
                </div>
                {parentUser && 
                    <div className='text-sm text-stone-400 '>
                        <p className='inline'>{'Respondendo à '}</p>
                        <p onClick={(e) => handleUserClick(e, parentUser)}
                            className='inline hover:cursor-pointer 
                            hover:underline text-stone-200'
                            >
                            @{parentUser.username}
                        </p>
                    </div>
                }
                <p className="text-xl my-1">{title}</p>
            </div>
            <div className="flex flex-col">
                {ingredients && ingredients.length > 0 && 
                    <div className="flex flex-col">
                        <p className="font-medium mt-1">
                            {'Ingredientes: '}
                        </p>
                        {ingredients.map((ingredient, index) => (
                            <p key={index} className="inline">  {
                                    ingredient.quantity + " " + 
                                    ingredient.unity + " " + 
                                    ingredient.name
                                }
                            </p>
                        ))}
                    </div>
                }
                {description && 
                    <div>
                        <p className="font-medium mt-2">
                            {'Descrição: '}
                        </p>
                        <p>{description}</p>
                    </div>
                }
                {file && 
                    <img 
                        className="rounded-xl max-h-[500px] mt-4" 
                        src={baseURL + '/upload/post/' + file} 
                        alt=''
                    />
                }
            </div>
        </div>
    )
}

export default PostBody;
