import moment from "moment";
import { request } from "../../utils/axios";

export const useFetchPostThread = id => {
    const fetchPost = async id => 
        request.get('/single', {
            params: { id }
            }).then(res => {
                const post = res.data[0];
                return {
                    ...post, 
                    ingredients: JSON.parse(post.ingredients),
                    date: moment(post.date).format('YYYY-MM-DD HH:mm:ss')
                }
            })

    
    return fetchThread = async () => {
        const postsArr = [];
        let post = await fetchPost(id);
        while(post.parent_id) {
            post = await fetchPost(post.parent_id);
            postsArr.unshift(post);
        }
        return postsArr;
    }
}
